import React from 'react';
import { random, names, animals } from '../words';
import { useEffect } from 'react';

interface Props {
    paragraphs: number,
}

const TextGenerator: React.FC<Props> = (props) => {

    const wordsArray:Array<string> = random.concat(animals).concat(names);
    const shuffledWords = shuffleArray(wordsArray)


    const paragraphs:any = [];

    useEffect(() => {
        let currentIndex = 0;
        for (let i = 0; i < props.paragraphs; i++) {

            const newParagraph = shuffledWords.slice(currentIndex, currentIndex + 100).join(' ');
            paragraphs.push(newParagraph);
            currentIndex+=100
        }

        console.log(paragraphs)
    }, [props.paragraphs])


    return (


        <div>
            {props.paragraphs}
            {/* <p>{mappedParagraphs}</p> */}
        </div>
    )
}

// https://stackoverflow.com/questions/2450954/how-to-randomize-shuffle-a-javascript-array
function shuffleArray(array: Array<string>) {
    for (var i = array.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
    return array;
}


export default TextGenerator;
