import logo from './logo.svg';
import './App.css';
import Form from './components/Form';
import TextGenerator from './components/TextGenerator';
import React, { Component } from 'react';
import './components/stylesheets/styles.css';
import avatarLogo from './avatar-logo.png'

interface State {
  paragraphs: number;
}


export default class App extends Component {

  state = {
    paragraphs: 0,
  }

  handleChange: any = (e: string): void => {
    this.setState({
        paragraphs: Number(e),
    })
}

  render() {
    return (
      <div className="App">
        <div>
          <img src={avatarLogo} /> 
          <h2>Lorem Ipsum Generator</h2>

          <Form handleChange={this.handleChange} />
          <h1>{this.state.paragraphs} paragraphs of premium quality avatar lorem ipsum</h1>
          <TextGenerator paragraphs={this.state.paragraphs} />
          </div>          
        </div>

    );
  }
}
