import React, { Component } from 'react';

interface Props {
    handleChange: () => void;
}


export default class Form extends Component<Props> {

    state = {
        paragraphs: 0
    }

    handleChange = (e: React.ChangeEvent<HTMLInputElement>): void => {
        this.setState({
            paragraphs: Number(e.target.value),
        })
    }

    handleSubmit = () => {

    }

    render() {
        return (
            <div>
                <input type="number" value={this.state.paragraphs} onChange={this.handleChange}></input>
                <button onClick={this.handleSubmit}>Go!</button>
            </div>
        )
    }
}
