import logo from './logo.svg';
import './App.css';
import Form from './components/Form';
import TextGenerator from './components/TextGenerator';
import React, { Component } from 'react';
import '../'

interface State {
  paragraphs: number;
}


export default class App extends Component {

  state = {
    paragraphs: 0,
  }

  handleChange: any = (e: string): void => {
    this.setState({
        paragraphs: Number(e),
    })
}

  render() {
    return (
      <div className="App">
      Avatar Lorem Ipsum Generator
      <Form handleChange={this.handleChange} />
      <h1>{this.state.paragraphs} paragraphs of premium quality avatar lorem ipsum</h1>
      <TextGenerator paragraphs={this.state.paragraphs} />
      </div>
    );
  }
}
