import React from 'react';
import { random, names, animals } from '../words';
import { useEffect, useState } from 'react';

interface Props {
    paragraphs: number,
}

const TextGenerator: React.FC<Props> = (props) => {

    const [generatedText, setGeneratedText] = useState('');

    const wordsArray:Array<string> = random.concat(animals).concat(names);


    useEffect((wordsArray) => {
        const paragraphs:any = [];
        for (let i = 0; i < props.paragraphs; i++) {
            const paragraph: any = [];
            for (let j = 0; j < 100; j++) {
                let word = wordsArray[Math.floor(Math.random() * wordsArray.length)];
                paragraph.push(word);
            }
            console.log(paragraph.join(' '));
        }

        setGeneratedText(paragraphs);
    }, [props.paragraphs]);


    return (

        <div>
            {props.paragraphs}
            {generatedText}
        </div>
    )
}

// https://stackoverflow.com/questions/2450954/how-to-randomize-shuffle-a-javascript-array
function shuffleArray(array: Array<string>) {
    for (var i = array.length - 1; i > 0; i--) {
        var j = Math.floor(Math.random() * (i + 1));
        var temp = array[i];
        array[i] = array[j];
        array[j] = temp;
    }
    return array;
}


export default TextGenerator;
