import React, { Component } from 'react';

interface Props {
    handleChange: (event: string) => void,
}


export default class Form extends Component<Props> {

    state = {
        paragraphs: 0
    }

    handleChange = (value: string) => {
        this.setState({paragraphs: Number(value)});
        this.props.handleChange(value);
    }

    render() {
        return (
            <div>
                <input type="number" value={this.state.paragraphs} onChange={event => this.handleChange(event.target.value)}></input>
            </div>
        )
    }
}
